--Declare (Khai báo trong SQL)
DECLARE 	@empName NVARCHAR(20), @empSSN AS DECIMAL, 		@empSalary DECIMAL=1000SELECT	@empName=empName, @empSalary=empSalaryFROM	[dbo].[tblEmployee]WHERE	empName=N'Mai Duy An'UPDATE	tblEmployeeSET	@empName=empName, @empSalary=empSalaryWHERE	empName=N'Mai Duy An'SELECT empname,empSalaryFROM [dbo].[tblEmployee]WHERE empname = N'Mai Duy An'--SET thông qua truy vấn SELECTDECLARE @MaxSalary intSELECT @MaxSalary = MAX(empSalary) FROM [dbo].[tblEmployee]--Lấy tuổi của giáo viên --Giáo viên lương cao nhấtSELECT empSSNFROM [dbo].[tblEmployee]WHERE empSalary in (				SELECT MAX(empSalary)				FROM [dbo].[tblEmployee])--Tính tuổi của giáo viênSELECT YEAR(GETDATE()) - YEAR(empBirthdate) AS [TuoiGiaoVien]FROM [dbo].[tblEmployee]WHERE empSSN = 30121050004--Tỉnh tuổi của giáo viên có lương cao nhất (Không dùng DECLARE)SELECT YEAR(GETDATE()) - YEAR(empBirthdate) AS [TuoiGiaoVien]FROM [dbo].[tblEmployee]WHERE empSSN = (SELECT empSSNFROM [dbo].[tblEmployee]WHERE empSalary in (				SELECT MAX(empSalary)				FROM [dbo].[tblEmployee]))--Tính tuổi của giáo viên có lương cao nhất (Dùng DECLARE)DECLARE @MaxSalaryEmpSSN char(15)SELECT @MaxSalaryEmpSSN = (SELECT empSSNFROM [dbo].[tblEmployee]WHERE empSalary in (				SELECT MAX(empSalary)				FROM [dbo].[tblEmployee]))SELECT YEAR(GETDATE()) - YEAR(empBirthdate) AS [TuoiGiaoVien]FROM [dbo].[tblEmployee]WHERE empSSN = @MaxSalaryEmpSSN--Xuất ra số lượng nhân viên của depnum 1--Số lượng nhân viên DECLARE @EmpID char (15) = 30121050004SELECT COUNT(supervisorSSN) as [TotalSup]FROM [dbo].[tblEmployee]WHERE empSSN = @EmpID--Xuất ra tên GV có lương thấp nhất SELECT MIN([empSalary]) FROM [dbo].[tblEmployee]DECLARE @MinSalaryTeacher INT;DECLARE @Name nvarchar(30);SELECT @MinSalaryTeacher = MIN([empSalary]) FROM [dbo].[tblEmployee]SELECT @Name = [empName] FROM [dbo].[tblEmployee] WHERE [empSalary] = @MinSalaryTeacherPRINT @Name PRINT @MinSalaryTeacher